query ($page : Int, $itemPerPage : Int) {
    getBlueApps(page : $page, itemsPerPage : $itemPerPage) {
        collection {
            id
            name {
                language
                text
                translations {
                    language
                    text
                }
            }
            description
            version
            status
            createdAt
            updatedAt
            managers {
                entities {
                    id
                    isManager
                }
                users {
                    id
                    firstname
                    lastname
                }
            }
            directory {
                id
                entityId
                name {
                    language
                    text
                    translations {
                        language
                        text
                    }
                }
                createdAt
                updatedAt
            }
        }
    }
}

